import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';
import Footer from "./components/Footer";
import { Box, Container, Grid, Link, SvgIcon, Typography } from '@mui/material';
import {fetchWeatherData} from "./services/OpenWeatherService"
import Search from "./components/Search"
import GitHubIcon from '@mui/icons-material/GitHub';
function App() {
    const [loading, setLoading]= useState(false)
  const searchChangeHandler = async (enteredData) => {
    const [latitude, longitude] = enteredData.value.split(' ');

    setLoading(true);

    // const currentDate = transformDateFormat();
    // const date = new Date();
    // let dt_now = Math.floor(date.getTime() / 1000);

    try {
      const [todayWeatherResponse, weekForecastResponse] =
        await fetchWeatherData(latitude, longitude);
        alert("todayweatherResponse",todayWeatherResponse)
        console.log("today", todayWeatherResponse);
        console.log("forecast",weekForecastResponse)
      // const all_today_forecasts_list = getTodayForecastWeather(
      //   weekForecastResponse,
      //   currentDate,
      //   dt_now
      // );

      // const all_week_forecasts_list = getWeekForecastWeather(
      //   weekForecastResponse,
      //   ALL_DESCRIPTIONS
      // );

      // setTodayForecast([...all_today_forecasts_list]);
      // setTodayWeather({ city: enteredData.label, ...todayWeatherResponse });
      // setWeekForecast({
      //   city: enteredData.label,
      //   list: all_week_forecasts_list,
      // });
    } catch (error) {
      //setError(true);
      console.log(error)
    }

    setLoading(false);
  };
  return (
    <Container
    sx={{
      maxWidth: { xs: '95%', sm: '80%', md: '1100px' },
      width: '100%',
      height: '100%',
      margin: '0 auto',
      padding: '1rem 0 3rem',
      marginBottom: '1rem',
      borderRadius: {
        xs: 'none',
        sm: '0 0 1rem 1rem',
      },
      boxShadow: {
        xs: 'none',
        sm: 'rgba(0,0,0, 0.5) 0px 10px 15px -3px, rgba(0,0,0, 0.5) 0px 4px 6px -2px',
      },
    }}
  >
    <Grid container columnSpacing={2}>
        <Grid item xs={12}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            sx={{
              width: '100%',
              marginBottom: '1rem',
            }}
          >
            <Link
              href="https://github.com"
              target="_blank"
              underline="none"
              sx={{ display: 'flex', marginLeft :'95%' }}
            >
              <GitHubIcon
                sx={{
                  fontSize: { xs: '20px', sm: '22px', md: '26px' },
                  color: 'white',
                  '&:hover': { color: '#2d95bd' },
                }}
              />
            </Link>
          </Box>
          <Search onSearchChange={searchChangeHandler} />
        </Grid>
        {/* {appContent} */}
      </Grid>
    <Footer/>
    </Container>
  );
}

export default App;
