import React from 'react'
import { Button } from '@mui/material'
import ArrowUpwardOutlinedIcon from '@mui/icons-material/ArrowUpwardOutlined';
import { animateScroll as scroll } from "react-scroll";
import KeyboardArrowUpOutlinedIcon from '@mui/icons-material/KeyboardArrowUpOutlined';
function ToTheTopButton() {
    const [hover, setHover] = React.useState(false)
    const [color, setColor] = React.useState('white')
    const [bg, setBg] = React.useState('black')
    const onHover = () => {
        setHover(p => !p)
    }
    const [scrollPosition, setScrollPosition] = React.useState(0);
    const handleScroll = () => {
        const position = window.pageYOffset;
        setScrollPosition(position);
        if (position > 900 && position < 1780 || position > 2648 && position < 3504 || position > 4307 && position < 5174) {
            setBg('black')
            setColor('white')
            
        }
        else {
            setBg('white')
            setColor('black')
            
        }
    };

    React.useEffect(() => {
        window.addEventListener('scroll', handleScroll, { passive: true });

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    React.useEffect(() => {
       handleScroll()
    }, [hover]);



    return (
        <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'flex-end', position: 'sticky', zIndex: 10, top: '92vh', bottom: 5 }}>
            {scrollPosition > 760 && <Button onMouseEnter={onHover} onMouseLeave={onHover} onClick={() => { scroll.scrollToTop() }} size='large' sx={{ "&:hover": { backgroundColor: bg },borderRadius: 100, background: bg, color: color, marginRight: '10px' }}>
                {hover?<ArrowUpwardOutlinedIcon />:<KeyboardArrowUpOutlinedIcon/>}
            </Button>}
           
        </div>
    )
}

export { ToTheTopButton }
