import React , { useState }from 'react';
import { Grid ,ToggleButtonGroup,ToggleButton} from '@mui/material';
//import { getDayMonthFromDate } from '../../../utilities/DatetimeUtils';
import { weatherIcon } from '../../../utils/IconsUtils';
import ErrorBox from '../../Reusable/ErrorBox';
import CityDateDetail from './CityDateDetail';
import TemperatureWeatherDetail from './TemperatureWeatherDetail';
import WeatherIconDetail from './WeatherIconDetail';
import Layout from '../../Reusable/Layout';
import Humidity from './Humidity'
let today = new Date();
let month=today.toLocaleString("en-US", { month: "long" })
let finalmon=month.substring(0,3);
let date=today.getDate();
let dayMonth=finalmon+date;



const Details = ({ data }) => {
  const [temp, setTemp]=useState(null);
  const noDataProvided =
    !data || Object.keys(data).length === 0 || data.cod === '404';

  let content = <ErrorBox flex="1" type="error" />;
  const handleToggleChange = (e, value) => {
    setTemp(value)
}
console.log(temp)
  if (!noDataProvided)
    content = (
      <> 
      <Grid xs={12}
      sx={{
        marginTop: '5%',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center'
      }}>
           <ToggleButtonGroup
  orientation="horizantal"
  value={temp}
      onChange={handleToggleChange }
      name="temp" 
      id="temp-select"
      exclusive={true}
>
  <ToggleButton  sx={{
                  fontSize: { xs: '12px', sm: '14px', md: '16px' },
                  color: 'white',
                  '&:hover': { color: '#000000' },
                }} value="celsius" aria-label="celsius">
  Celsius
  </ToggleButton>
  <ToggleButton sx={{
                  fontSize: { xs: '12px', sm: '14px', md: '26==16px' },
                  color: 'white',
                  '&:hover': { color: '#000000' },
                }} value="farhenheit" aria-label="farhenheit">
  Farhenheit
  </ToggleButton>
</ToggleButtonGroup>
</Grid>
        <Grid
          item
          xs={6}
          sx={{
            height: '80px',
          }}
        >
          <CityDateDetail city={data.city} date={dayMonth} />
        </Grid>
        <Grid
          item
          xs={6}
          sx={{
            height: '80px',
          }}
        >
          <TemperatureWeatherDetail
            temperature={data.main.temp}
            description={data.weather[0].description}
          />
        </Grid>
        <Grid
          item
          xs={6}
          sx={{
            height: '80px',
          }}
        >
          <Humidity
          title="Humidity"
            humidity={`${Math.round(data.main.humidity)} %`}
          />
        </Grid>
        <Grid
          item
          xs={6}
          sx={{
            height: '80px',
          }}
        >
          <Humidity
          title="wind"
            humidity={`${data.wind.speed} M/S`}
          />
        </Grid>
        <Grid
          item
          xs={6}
          sx={{
            height: '80px',
          }}
        >
          <Humidity
          title="Clouds"
            humidity={`${Math.round(data.clouds.all)} %`}
          />
        </Grid>
        <Grid
          item
          xs={6}
          sx={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '80px',
          }}
        >
          <WeatherIconDetail src={weatherIcon(`${data.weather[0].icon}.png`)} />
        </Grid>
      </>
    );

  return <Layout title="CURRENT WEATHER" content={content} />;
};

export default Details;
