  //     <ErrorBox
  //       margin="3rem auto"
  //       flex="inherit"
  //       errorMessage="Something went wrong"
  //     />
  //   );
  // }

  // if (loading) {
  //   appContent = (
  //     <Box
  //       sx={{
  //         display: 'flex',
  //         justifyContent: 'center',
  //         alignItems: 'center',
